package edu.wlxy.ch02;

public class ForDemo1 {
	/*
	 * 输出5遍“好好学习，天天向上！”

	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=0;
		while(i<5){
		System.out.println("好好学习！");
		i++;
		}   
      //利用for结构进行改写
		for(int j=0;j<5;j++){
			System.out.println("疫情早日结束。");
		}
		
	}

}
